import React from 'react';
import PageNavbar from './PageNavbar';
import BestGenreRow from './BestGenreRow';
import '../style/BestGenres.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class BestGenre extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			country1: "",
			country2: "",
			selectedDecade: "",
			decades: [],
			genres: []
		};

		this.submitDecade = this.submitDecade.bind(this);
		this.handleChange = this.handleChange.bind(this);
		this.handleChange2 = this.handleChange2.bind(this);
	}

	/* ---- Q3a (Best Genres) ---- */
	componentDidMount() {

		console.log("country drop downs");

		// Send an HTTP request to the server.
	    fetch("http://localhost:8081/decades", {
	      method: 'GET' // The type of HTTP request.
	    })
	      .then(res => res.json()) // Convert the response data to a JSON.
	      .then(decadesList => {
	        if (!decadesList) return;
	        // Map each genreObj in genreList to an HTML element:
	        // A button which triggers the showMovies function for each genre.
	        //console.log("hello");
	        //let h = 'hello';

	        //the field name should be the column name
	        let decadesDivs = decadesList.map((decObj, i) =>
	          <option value ={decObj.country_name}>{decObj.country_name}</option>
	        );

	        // Set the state of the genres list to the value returned by the HTTP response from the server.
	        this.setState({
	          decades: decadesDivs
	        })
	      })
	      .catch(err => console.log(err))	// Print the error if there is one.



	}

	handleChange(e) {
		this.setState({
			country1: e.target.value
		});
	}

	handleChange2(e) {
		this.setState({
			country2: e.target.value
		});
	}

	/* ---- Q3b (Best Genres) ---- */
	submitDecade() {

		let year = this.state.selectedDecade;
		let country1 = this.state.country1;
		let country2 = this.state.country2;
		console.log(country1);
		console.log(country2);
		let route = "http://localhost:8081/bestgenres/" + year;
		console.log(route);

		fetch(route, {
	      method: 'GET' // The type of HTTP request.
	    })
	      .then(res => res.json()) // Convert the response data to a JSON.
	      .then(genreAvgList => {
	        if (!genreAvgList) return;
	        // Map each genreObj in genreList to an HTML element:
	        // A button which triggers the showMovies function for each genre.
	        //console.log("hello");
	        //let h = 'hello';
	        let bestRowDivs = genreAvgList.map((bestObj, i) =>
	        	<BestGenreRow genre={bestObj.genre} rating={bestObj.avg_rating} />
	        );

	        // Set the state of the genres list to the value returned by the HTTP response from the server.
	        this.setState({
	          genres: bestRowDivs
	        })
	      })
	      .catch(err => console.log(err))	// Print the error if there is one.


	}

	render() {

		return (
			<div className="Country Relationships">
				<PageNavbar active="bestgenres" />

				<div className="container bestgenres-container">
			      <div className="jumbotron">
			        <div className="h5">Countries</div>

			        <p> 
			        </p>

			        <div className="years-container">
			          <div className="dropdown-container">
			            <select value={this.state.country1} onChange={this.handleChange} className="dropdown" id="decadesDropdown">
			            	<option select value> -- select an option -- </option>
			            	{this.state.decades}
			            </select>
			          </div>
			          <div className="dropdown-container2">
			            <select value={this.state.country2} onChange={this.handleChange2} className="dropdown2" id="decadesDropdown2">
			            	<option select value> -- select an option -- </option>
			            	{this.state.decades}
			            </select>
			            <button className="submit-btn" id="decadesSubmitBtn" onClick={this.submitDecade}>Submit</button>
			          </div>
			        </div>

			      </div>
			      <div className="jumbotron">
			        <div className="movies-container">
			          <div className="movie">
			            <div className="header"><strong>Genre</strong></div>
			            <div className="header"><strong>Average Rating</strong></div>
			          </div>
			          <div className="movies-container" id="results">
			            {this.state.genres}
			          </div>
			        </div>
			      </div>
			    </div>
			</div>
		);
	}
}
